select rr.IDuser, rr.IDitem, rr.rating, rr.timestamp from user_item_rating as rr, (select i.IDitem, i.titre, i.url, i.inconnu, i.`action`, i.adventure, i.animation, i.jeuneuse, i.comedie, i.crime, i.documentaire, i.drame, i.fantaisie, i.fim_noir, i.horreur, i.musical, i.mysterieux, i.romance, i.western, i.fiction, i.suspense, i.guerre from item as i, (select distinct IDitem, count(*) nbr  from user_item_rating, u where u.IDuser = user_item_rating.IDuser group by IDitem having nbr between 3 and 10 limit 300) as t where t.IDitem = i.IDitem) r , u where r.IDitem = rr.IDitem and rr.IDuser = u.IDuser